mapper.update:
update message
set m_sender_del_check = 1
where m_no=#{m_no}

update message
set m_receiver_del_check = 1
where m_no=#{m_no}

update message
set m_status = 1
where m_no=#{m_no}

mapper.select:
select nickname
from member
where member.id = #{m_receiver_Id}

select nickname
from member
where member.id = #{m_sender_Id}
